import { useState, useCallback } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import styled from 'styled-components';
import './App.css';
import PersonList from './components/PersonList';
import RoomList from './components/RoomList';

// Sample data - replace with your actual data
const samplePeople = [
  { id: 'p1', name: 'John Smith' },
  { id: 'p2', name: 'Jane Doe' },
  { id: 'p3', name: 'Bob Johnson' },
  { id: 'p4', name: 'Alice Williams' },
  { id: 'p5', name: 'David Brown' },
  { id: 'p6', name: 'Sarah Miller' },
  { id: 'p7', name: 'Michael Davis' },
  { id: 'p8', name: 'Emily Wilson' },
];

const sampleRooms = [
  { id: 'r1', name: 'Suite 101 - Hilton', capacity: 2, guests: [] },
  { id: 'r2', name: 'Room 202 - Marriott', capacity: 2, guests: [] },
  { id: 'r3', name: 'Villa 303 - Resort', capacity: 4, guests: [] },
  { id: 'r4', name: 'Cabin 404 - Lodge', capacity: 3, guests: [] },
];

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: #f5f7fa;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
`;

const Header = styled.header`
  background-color: #6366f1;
  color: white;
  padding: 1.5rem;
  text-align: center;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const MainContainer = styled.main`
  display: flex;
  flex: 1;
  padding: 2rem;
  gap: 2rem;
  overflow: hidden;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

function App() {
  const [people, setPeople] = useState(samplePeople);
  const [rooms, setRooms] = useState(sampleRooms);
  const [unassignedPeople, setUnassignedPeople] = useState(samplePeople);

  const handleAssignPerson = useCallback((personId, roomId) => {
    // Find the person
    const person = unassignedPeople.find(p => p.id === personId);
    if (!person) return;

    // Remove from unassigned list
    setUnassignedPeople(prev => prev.filter(p => p.id !== personId));

    // Add to room
    setRooms(prev => prev.map(room => {
      if (room.id === roomId) {
        return {
          ...room,
          guests: [...room.guests, person]
        };
      }
      return room;
    }));
  }, [unassignedPeople]);

  const handleUnassignPerson = useCallback((personId, roomId) => {
    // Find the room
    const room = rooms.find(r => r.id === roomId);
    if (!room) return;

    // Find the person in the room
    const person = room.guests.find(p => p.id === personId);
    if (!person) return;

    // Remove from room
    setRooms(prev => prev.map(r => {
      if (r.id === roomId) {
        return {
          ...r,
          guests: r.guests.filter(p => p.id !== personId)
        };
      }
      return r;
    }));

    // Add back to unassigned list
    setUnassignedPeople(prev => [...prev, person]);
  }, [rooms]);

  return (
    <DndProvider backend={HTML5Backend}>
      <AppContainer>
        <Header>
          <h1>Wedding Room Planner</h1>
        </Header>
        <MainContainer>
          <PersonList 
            people={unassignedPeople} 
          />
          <RoomList 
            rooms={rooms} 
            onAssignPerson={handleAssignPerson}
            onUnassignPerson={handleUnassignPerson}
          />
        </MainContainer>
      </AppContainer>
    </DndProvider>
  );
}

export default App;
