import { useDrop } from 'react-dnd';
import styled from 'styled-components';
import Person from './Person';

const RoomContainer = styled.div`
  background-color: white;
  border-radius: 0.5rem;
  padding: 1.25rem;
  margin-bottom: 1rem;
  box-shadow: 0 2px 8px rgba(210, 165, 86, 0.1);
  transition: all 0.2s;
  border: 1px solid #fff0db;

  ${props => props.isOver && `
    box-shadow: 0 0 0 2px #f9a826, 0 4px 8px rgba(249, 168, 38, 0.2);
    transform: translateY(-2px);
    background-color: #fffaf0;
  `}
`;

const RoomHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
  border-bottom: 1px solid #f9d772;
  padding-bottom: 0.75rem;
`;

const RoomName = styled.h3`
  margin: 0;
  color: #5c4226;
  font-weight: 600;
  font-family: 'Playfair Display', serif;
`;

const RoomCapacity = styled.div`
  background-color: ${props => {
    if (props.filled > props.capacity) return '#fef3c7';
    if (props.filled === props.capacity) return '#ecfdf5';
    return '#fff1e6';
  }};

  color: ${props => {
    if (props.filled > props.capacity) return '#92400e';
    if (props.filled === props.capacity) return '#065f46';
    return '#9a3412';
  }};

  padding: 0.25rem 0.75rem;
  border-radius: 2rem;
  font-size: 0.875rem;
  font-weight: 500;
  font-family: 'Montserrat', sans-serif;
  border: 1px solid ${props => {
    if (props.filled > props.capacity) return '#fbbf24';
    if (props.filled === props.capacity) return '#10b981';
    return '#f97316';
  }};
`;

const GuestList = styled.div`
  min-height: 100px;
  background-color: ${props => props.isOver ? '#fff8f0' : 'transparent'};
  border-radius: 0.25rem;
  transition: background-color 0.2s;
  padding: ${props => props.isEmpty ? '0' : '0.5rem 0'};
`;

const EmptyState = styled.div`
  color: #d4af37;
  text-align: center;
  padding: 1.5rem;
  border: 2px dashed #f9d772;
  border-radius: 0.25rem;
  font-style: italic;
  background-color: #fffaf0;
  font-family: 'Montserrat', sans-serif;
`;

function Room({ id, name, capacity, guests, onAssignPerson, onUnassignPerson }) {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'PERSON',
    drop: (item) => {
      onAssignPerson(item.id, id);
    },
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }), [id, onAssignPerson]);

  return (
    <RoomContainer isOver={isOver}>
      <RoomHeader>
        <RoomName>{name}</RoomName>
        <RoomCapacity 
          filled={guests.length} 
          capacity={capacity}
        >
          {guests.length}/{capacity}
        </RoomCapacity>
      </RoomHeader>

      <GuestList 
        ref={drop} 
        isOver={isOver}
        isEmpty={guests.length === 0}
      >
        {guests.length > 0 ? (
          guests.map(guest => (
            <Person 
              key={guest.id}
              id={guest.id}
              name={guest.name}
              inRoom={true}
              roomId={id}
              onUnassign={onUnassignPerson}
            />
          ))
        ) : (
          <EmptyState>Trage invita»õii aici</EmptyState>
        )}
      </GuestList>
    </RoomContainer>
  );
}

export default Room;
